import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import ReactScrollableFeed from "react-scrollable-feed";
import "./chat.scss";
let socket;
const CONNECTION = "localhost:8080/";
function Chat() {
  console.log();
  const [isLoggedIn, setIsloggedIn] = useState(false);
  const [room, setRoom] = useState("");
  const [message, setMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [username, setUsername] = useState("");
  const [userInfo, setUserInfo] = useState({});
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    socket = io(CONNECTION);
  }, [CONNECTION]);

  const roomConnection = () => {
    socket.emit("join", room, userInfo.username);
    console.log(room, messageList, userInfo.username);
    setIsloggedIn(true);
  };
  const sendMessage = () => {
    let messageContent = {
      room: room,
      content: {
        username: userInfo.username,
        message: message,
        owner: userInfo,
      },
    };

    // send message

    socket.emit("send_message", messageContent);
    setMessageList([...messageList, messageContent.content]);
  };

  //receive message

  useEffect(() => {
    socket.on("receive_message", (data) => {
      console.log(data);
      setMessageList([...messageList, data]);
    });
  }, []);

  const getMessage = async () => {
    const response = await fetch("http://localhost:8080/message");
    const res = await response.json();
    console.log(res);
    setMessages(res);
  };

  const authToken = sessionStorage.getItem("authToken");

  const reqOptions = {
    headers: {
      authorization: `Bearer ${authToken}`,
    },
  };
  const getProfile = async () => {
    const response = await fetch(
      "http://localhost:8080/user/profile",
      reqOptions
    );

    const userInfo = await response.json();

    setUserInfo(userInfo);
  };
  useEffect(() => {
    getProfile();
    getMessage();
  }, []);

  return (
    <div className="container">
      <div className="page-title">
        <div className="row gutters">
          <div className="col-xl-6 col-lg-6 col-md-6 col-sm-12 col-12">
            <h5 className="title">Chat App</h5>
          </div>
          <div className="col-xl-6 col-lg-6 col-md-6 col-sm-12 col-12"> </div>
        </div>
      </div>

      <div className="content-wrapper">
        <div className="row gutters">
          <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
            <div className="card m-0">
              <div className="row no-gutters">
                <div className="col-xl-4 col-lg-4 col-md-4 col-sm-3 col-3">
                  <div className="users-container">
                    <div className="chat-search-box">
                      <div className="input-group">
                        <input
                          className="form-control"
                          placeholder="Select room..."
                          onChange={(e) => setRoom(e.target.value)}
                        />
                        <div className="input-group-btn">
                          <button
                            type="button"
                            className="btn btn-info"
                            onClick={roomConnection}
                          >
                            <i className="fa fa-search"></i>
                          </button>
                        </div>
                      </div>
                    </div>
                    <ul className="users">
                      <li class="person active-user" data-chat="person2">
                        <div class="user">
                          <img
                            src="https://www.bootdey.com/img/Content/avatar/avatar2.png"
                            alt="Retail Admin"
                          />
                          <span class="status away"></span>
                        </div>
                        <p class="name-time">
                          <span class="name">{userInfo.username}</span>
                          <span class="time">12/02/2019</span>
                        </p>
                      </li>
                    </ul>
                  </div>
                </div>
                <div class="col-xl-8 col-lg-8 col-md-8 col-sm-9 col-9">
                  <div class="selected-user">
                    <span>
                      To: <span class="name">Emily Russell</span>
                      {message.map((msg) => {
                        console.log(msg);
                        return <div></div>;
                      })}
                    </span>
                  </div>

                  <div class="chat-container">
                    <div className="messages">
                      {messages.map((val, key) => {
                        return val.map((message) => {
                          console.log(val);
                          return (
                            <div
                              className="messageContainer"
                              id={
                                message.username == username ? "You" : "Other"
                              }
                            >
                              <div className="messageIndividual">
                                {message.username} : {message.message}
                              </div>
                            </div>
                          );
                        });
                      })}
                    </div>
                    <ul class="chat-box chatContainerScroll"></ul>
                    <div class="form-group mt-3 mb-0">
                      <input
                        type="text"
                        placeholder="Message..."
                        onChange={(e) => {
                          setMessage(e.target.value);
                        }}
                      />
                      <button onClick={sendMessage}>Send</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Chat;
