import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import "./chat.scss";
let socket;
const CONNECTION = "localhost:8080/";
function Chat() {
  const [isLoggedIn, setIsloggedIn] = useState(false);
  const [room, setRoom] = useState("");
  const [message, setMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [username, setUsername] = useState("");
  useEffect(() => {
    socket = io(CONNECTION);
  }, [CONNECTION]);

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList([...messageList, data]);
    });
  }, []);
  const roomConnection = () => {
    socket.emit("join", room);
    setIsloggedIn(true);
  };
  const sendMessage = () => {
    let messageContent = {
      room: room,
      content: {
        username: username,
        message: message,
      },
    };

    socket.emit("send_message", messageContent);
    setMessageList([...messageList, messageContent.content]);
    setMessage("");
  };

  const reqOptions = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: { username: username, room: room },
  };
  const registerUser = async (e) => {
    e.preventDefault();
    const response = fetch("http://localhost:8080/user", reqOptions);
    const user = await response.json();
    console.log(user);
  };
  return (
    <div className="chat">
      {!isLoggedIn ? (
        <form onSubmit={registerUser}>
          <div className="logIn">
            <div className="inputs">
              <input
                name="username"
                type="text"
                placeholder="Name..."
                onChange={(e) => {
                  setUsername(e.target.value);
                }}
              />
              <input
                name="room"
                type="text"
                placeholder="Room..."
                onChange={(e) => {
                  setRoom(e.target.value);
                }}
              />
            </div>
            <button onClick={roomConnection} type="submit">
              Enter Chat
            </button>
          </div>
        </form>
      ) : (
        <div className="chatContainer">
          <div className="messages">
            {messageList.map((val, key) => {
              console.log(val);
              return (
                <div
                  className="messageContainer"
                  id={val.username === username ? "You" : "Other"}
                >
                  <div className="messageIndividual">
                    {val.username === username ? "You" : "Other"}:{val.message}
                  </div>
                </div>
              );
            })}
          </div>

          <div className="messageInputs">
            <input
              type="text"
              placeholder="Message..."
              onChange={(e) => {
                setMessage(e.target.value);
              }}
            />

            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}
export default Chat;
